import React from 'react'
import FirstPageIcon from '@material-ui/icons/FirstPage';
import LastPageIcon from '@material-ui/icons/LastPage';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';

import './Pagination.css'

function Pagination({setPage, currentPage, pages}){

    if(pages===1) return null;

    let hidden = currentPage === 1 ? 'hidden' : ''

    let range = []

    if(currentPage < pages - 5){
        for (let i = currentPage; i < currentPage + 5; ++i) {
            range.push(i);
          }
    }
    else{
        for (let i = pages - 4; i < pages ; ++i) {
            range.push(i);
          }
    }

    const clickLastPage = ev => {
        ev.preventDefault();
        setPage(pages);
      };

    const clickFirstPage = ev => {
        ev.preventDefault();
        setPage(1)
    }

    const clickNextPage = ev => {
        ev.preventDefault();
        setPage(currentPage - 1)
    }


    return (<div className='pages'>
        <ul className="pagination">
            {currentPage === 1 ? " " : ( 
                <>
                <li className='page' ><FirstPageIcon onClick={clickFirstPage}/></li>
                <li className='page'><ArrowBackIosIcon onClick={clickNextPage}/></li>
                </>
            )}
            {range.map( num => {
                const clickPage = ev => {
                    ev.preventDefault();
                    setPage(num);
                  };
                return(<li 
                className= { num===currentPage ? 'page pg-active' : 'page' }
                onClick={clickPage}
                >{num}</li>
            )}
            )}
            <div>....</div>
            <li className= { pages===currentPage ? 'page pg-active' : 'page' } onClick={clickLastPage}>{pages}</li>
        </ul>
    </div>)
}

export default Pagination
