import { Card, CardContent, CardMedia, CircularProgress, Typography } from '@material-ui/core'
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { get_images } from '../actions/model'
import {Carousel, CarouselSlide} from 'react-material-ui-carousel'

function ImgCarousel({id}){
    const dispatch = useDispatch()

    let loaded = useSelector(st => st.models.images !== undefined)
    const images = useSelector(st => st.models.images)

    useEffect(function(){
        dispatch(get_images(id))
    },[id, dispatch])

    if(!loaded) return <CircularProgress/>

    return(
   <Carousel>
       {images.map( image => (
           <CarouselSlide key={image}>
           <Card>
               <CardMedia
                   image={image.sizes[12]}
                   title={image.name}
                   style={{
                       paddingTop: '25%',
                   }}
               />
               <CardContent>
                   <Typography>{title}</Typography>
               </CardContent>
           </Card>
       </CarouselSlide>
       ))}
   </Carousel>)
}

export default ImgCarousel
